

// ANIMAÇÃO COM SCROLL
$(document).ready(function () {
  const ScrollDown = ScrollReveal({
    origin: "bottom",
    distance: "45px",
    duration: 960,
    reset: true,
    easing: "ease-out",
    
  });
  ScrollDown.reveal(".scroll-down", { delay: 2 });
  ScrollDown.reveal(".scroll-down-delay-3", { delay: 3 });
  ScrollDown.reveal(".scroll-down-delay-2", { delay: 1 });
  const ScrollLeft = ScrollReveal({
    origin: "left",
    distance: "45px",
    duration: 660,
    reset: true,
    easing: "ease-out",
  });
  ScrollLeft.reveal(".scroll-left", { delay: 1 });
  const ScrollRight = ScrollReveal({
    origin: "right",
    distance: "45px",
    duration: 660,
    reset: true,
    easing: "ease-out",
  });
  ScrollRight.reveal(".scroll-right", { delay: 1 });
});

