$('.owl-carousel').owlCarousel({
    loop:true,
    margin:10,
    nav:true,
   autoplay:true, 
    navigation: true,
  navText: [$('.owl-next'),$('.owl-prev')],
    responsive:{
        0:{
            items:1
        },
        600:{
            items:3
        },
        1000:{
            items:4
        }
    }
})
//VOLTAR AO TOPO
$(document).ready(function () {
    $(window).scroll(function () {
      let windowScroll = $(window).scrollTop();
      if (windowScroll > 750) {
        $(".topo").fadeIn(300);
      } else {
        $(".topo").fadeOut(300);
      }
    });
  
    $(".topo").click(function () {
      $("html,body").animate({
        scrollTop: $("html").offset().top,
      });
      return false;
    });
  });
  /* SCROLL SUAVE */

  $('.navbar a[href^="#"]').on('click', function(e) {
    e.preventDefault();
    var id = $(this).attr('href'),
        targetOffset = $(id).offset().top;
        
    $('html, body').animate({ 
      scrollTop: targetOffset - 40
      
    }, 2500);
  });
